import { useType } from "@hex-engine/core";
import { useUpdate } from "../Hooks";
/** This Component can be used to check how far the current time is from some desired time in the future. */

function Timer() {
  useType(Timer);
  var target = 0;
  useUpdate(function (delta) {
    target -= delta;
  });
  return {
    get target() {
      return target;
    },

    setToTimeFromNow: function setToTimeFromNow(msFromNow) {
      target = msFromNow;
    },
    distanceFromSetTime: function distanceFromSetTime() {
      return target;
    },
    hasReachedSetTime: function hasReachedSetTime() {
      return target < 0;
    }
  };
}

export default Timer;