import { createRoot, useEntityName, useNewComponent } from "..";
test("name set", function () {
  function Something() {
    useEntityName("Bob");
  }

  var ent = createRoot(Something);
  expect(ent.name).toBe("Bob");
});
test("name get", function () {
  function Something() {
    useEntityName("Bob");
  }

  var name = null;
  createRoot(function () {
    useNewComponent(Something);
    useNewComponent(function () {
      name = useEntityName();
    });
  });
  expect(name).toBe("Bob");
});